function disqus() {
    if (!disqus_loaded)  {
        disqus_loaded = true;
        var e = document.createElement("script");
        e.type = "text/javascript";
        e.async = true;
        e.src = "//" + disqus_shortname + ".disqus.com/blogger_item.js";
        (document.getElementsByTagName("head")[0] || document.getElementsByTagName("body")[0])
        .appendChild(e);
    }
};
